#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn floor_zero_0(){
        assert_eq!(what_floor("(())"),0);
    }
    
    #[test]
    fn floor_zero_1(){
        assert_eq!(what_floor("()()"),0);
    }

    #[test]
    fn floor_three_0(){
        assert_eq!(what_floor("((("),3)
    }

    #[test]
    fn floor_three_1(){
        assert_eq!(what_floor("(()(()("),3)
    }

    #[test]
    fn floor_three_2(){
        assert_eq!(what_floor("))((((("),3)
    }

    #[test]
    fn floor_neg_one_0(){
        assert_eq!(what_floor("())"),-1)
    }

    #[test]
    fn floor_neg_one_1(){
        assert_eq!(what_floor("))("),-1)
    }

    #[test]
    fn floor_neg_three_0(){
        assert_eq!(what_floor(")))"),-3)
    }

    #[test]
    fn floor_neg_three_1(){
        assert_eq!(what_floor(")())())"),-3)
    }


}


fn what_floor(instructions : &str) -> i32
{
    let mut floor = 0;
    let instructions = String::from(instructions);
    for instruction in instructions.chars() {
        match instruction {
            '(' => floor +=1,
            ')' => floor -=1,
            _ => panic!("Unexpected instruction")
        }
    }
    


    floor
}

