#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn pos_one(){
        assert_eq!(first_entry_of_basement(")"),1);
    }

    #[test]
    fn pos_five(){
        assert_eq!(first_entry_of_basement("()())"),5);
    }


}


fn first_entry_of_basement(instructions: &str) -> usize{
    let mut position: usize = 0;
    let mut floor = 0;
    for (i, instruction) in instructions.chars().enumerate(){
        match instruction {
            '(' => floor +=1,
            ')' => floor -=1,
            _ => panic!("Unexpected instruction")
        }
        if floor == -1 {
            position = i+1;
            return position
        }
    }
    position
}

